import { useApolloClient, useQuery } from "@apollo/client";
import {
    Field,
    FinalForm,
    FinalFormInput,
    FinalFormSaveSplitButton,
    FinalFormSubmitEvent,
    MainContent,
    Toolbar,
    ToolbarActions,
    ToolbarFillSpace,
    ToolbarItem,
    ToolbarTitleItem,
    useFormApiRef,
    useStackApi,
    useStackSwitchApi,
} from "@comet/admin";
import { ArrowLeft } from "@comet/admin-icons";
import { EditPageLayout, queryUpdatedAt, resolveHasSaveConflict, useFormSaveConflict } from "@comet/cms-admin";
import { CircularProgress, IconButton } from "@mui/material";
import { FormApi } from "final-form";
import { filter } from "graphql-anywhere";
import React from "react";
import { FormattedMessage } from "react-intl";

import { createCustomerMutation, customerFormFragment, customerFormQuery, updateCustomerMutation } from "./CustomerForm.gql";
import {
    GQLCreateCustomerMutation,
    GQLCreateCustomerMutationVariables,
    GQLCustomerFormFragment,
    GQLCustomerFormQuery,
    GQLCustomerFormQueryVariables,
    GQLUpdateCustomerMutation,
    GQLUpdateCustomerMutationVariables,
} from "./CustomerForm.gql.generated";

type FormValues = GQLCustomerFormFragment;

interface FormProps {
    id?: string;
}

export function CustomerForm({ id }: FormProps): React.ReactElement {
    const stackApi = useStackApi();
    const client = useApolloClient();
    const mode = id ? "edit" : "add";
    const formApiRef = useFormApiRef<FormValues>();
    const stackSwitchApi = useStackSwitchApi();

    const { data, error, loading, refetch } = useQuery<GQLCustomerFormQuery, GQLCustomerFormQueryVariables>(
        customerFormQuery,
        id ? { variables: { id } } : { skip: true },
    );

    const initialValues = React.useMemo<Partial<FormValues>>(
        () =>
            data?.customer
                ? {
                      ...filter<GQLCustomerFormFragment>(customerFormFragment, data.customer),
                  }
                : {},
        [data],
    );

    const saveConflict = useFormSaveConflict({
        checkConflict: async () => {
            const updatedAt = await queryUpdatedAt(client, "customer", id);
            return resolveHasSaveConflict(data?.customer.updatedAt, updatedAt);
        },
        formApiRef,
        loadLatestVersion: async () => {
            await refetch();
        },
    });

    const handleSubmit = async (state: FormValues, form: FormApi<FormValues>, event: FinalFormSubmitEvent) => {
        if (await saveConflict.checkForConflicts()) {
            throw new Error("Conflicts detected");
        }

        const output = {
            ...state,
        };

        if (mode === "edit") {
            if (!id) {
                throw new Error("Missing id in edit mode");
            }
            await client.mutate<GQLUpdateCustomerMutation, GQLUpdateCustomerMutationVariables>({
                mutation: updateCustomerMutation,
                variables: { id, input: output, lastUpdatedAt: data?.customer?.updatedAt },
            });
        } else {
            const { data: mutationReponse } = await client.mutate<GQLCreateCustomerMutation, GQLCreateCustomerMutationVariables>({
                mutation: createCustomerMutation,
                variables: { input: output },
            });
            if (!event.navigatingBack) {
                const id = mutationReponse?.createCustomer.id;
                if (id) {
                    setTimeout(() => {
                        stackSwitchApi.activatePage("edit", id);
                    });
                }
            }
        }
    };

    if (error) throw error;

    if (loading) {
        return <CircularProgress />;
    }

    return (
        <FinalForm<FormValues>
            apiRef={formApiRef}
            onSubmit={handleSubmit}
            mode={mode}
            initialValues={initialValues}
            onAfterSubmit={(values, form) => {
                //don't go back automatically
            }}
        >
            {({ values }) => (
                <EditPageLayout>
                    {saveConflict.dialogs}
                    <Toolbar>
                        <ToolbarItem>
                            <IconButton onClick={stackApi?.goBack}>
                                <ArrowLeft />
                            </IconButton>
                        </ToolbarItem>
                        <ToolbarTitleItem>
                            {mode == "edit" ? (
                                <FormattedMessage id="customers.editCustomer" defaultMessage="Edit Customer" />
                            ) : (
                                <FormattedMessage id="customers.newCustomer" defaultMessage="New Customer" />
                            )}
                        </ToolbarTitleItem>
                        <ToolbarFillSpace />
                        <ToolbarActions>
                            <FinalFormSaveSplitButton />
                        </ToolbarActions>
                    </Toolbar>
                    <MainContent>
                        <Field
                            required
                            fullWidth
                            name="firstname"
                            component={FinalFormInput}
                            label={<FormattedMessage id="customer.firstname" defaultMessage="Firstname" />}
                        />
                        <Field
                            required
                            fullWidth
                            name="lastname"
                            component={FinalFormInput}
                            label={<FormattedMessage id="customer.lastname" defaultMessage="Lastname" />}
                        />
                        <Field
                            required
                            fullWidth
                            name="email"
                            component={FinalFormInput}
                            label={<FormattedMessage id="customer.email" defaultMessage="Email" />}
                        />
                        <Field
                            required
                            fullWidth
                            name="address"
                            component={FinalFormInput}
                            label={<FormattedMessage id="customer.address" defaultMessage="Address" />}
                        />
                    </MainContent>
                </EditPageLayout>
            )}
        </FinalForm>
    );
}
